import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from "react-router-dom";
import Login from "./components/Login";
import SuperAdminDashboard from "./components/SuperAdminDashboard";
import AdminDashboard from "./components/AdminDashboard";
import NewsEntryDashboard from "./components/NewsEntryDashboard";
import ManageUsers from "./components/ManageUsers";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState("");

  // Function to handle successful login
  const handleLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role);
  };

  // Function to handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    setIsLoggedIn(false);
    setUserRole("");
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          {isLoggedIn ? (
            <Redirect to="/dashboard" />
          ) : (
            <Login onLogin={handleLogin} />
          )}
        </Route>
        <Route path="/dashboard">
          {!isLoggedIn ? (
            <Redirect to="/" />
          ) : (
            <>
              {userRole === "SuperAdmin" && (
                <SuperAdminDashboard onLogout={handleLogout} />
              )}
              {userRole === "Admin" && (
                <AdminDashboard onLogout={handleLogout} />
              )}
              {userRole === "NewsEntry" && (
                <NewsEntryDashboard onLogout={handleLogout} />
              )}
            </>
          )}
        </Route>
        <Route exact path="/dashboard/manageusers" component={ManageUsers} />
        {/* Add other routes for different dashboards here */}
        <Route path="*">
          <Redirect to="/" />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
