import React from "react";
import { BiTable, BiGrid, BiCategory } from "react-icons/bi";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faChartLine } from "@fortawesome/free-solid-svg-icons";

const Sidebar = ({ userRole }) => {
  const sidebarLinks = [
    { title: "Categories", icon: <BiCategory size={16} />, link: "/" },
    {
      title: "Tags",
      icon: <FontAwesomeIcon icon={fa} />,
      link: "/dashboard",
    },
    { title: "Orders", icon: <BiTable size={16} />, link: "/orders" },
    { title: "Products", icon: <BiGrid size={16} />, link: "/products" },
    {
      title: "Customers",
      icon: <FontAwesomeIcon icon={faUser} />,
      link: "/customers",
    },
  ];
  const getSidebarLinks = () => {
    const filteredLinks = sidebarLinks.filter((link) => {
      if (userRole === "SuperAdmin") {
        return true;
      } else if (userRole === "Admin") {
        return link.title !== "Users";
      } else {
        return link.title === "Dashboard";
      }
    });

    return (
      <ul className="nav flex-column mb-auto">
        {filteredLinks.map((link) => (
          <li className="nav-item" key={link.title}>
            <a
              href={link.link}
              className={`nav-link ${
                link.title === "Dashboard" ? "active" : "link-dark"
              }`}
              aria-current={link.title === "Dashboard" ? "page" : undefined}
            >
              <span className="me-2">{link.icon}</span>
              {link.title}
            </a>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div
      className="d-flex flex-column flex-shrink-0 p-3 bg-light"
      style={{ width: "280px" }}
    >
      <a
        href="/"
        className="d-flex align-items-center mb-3 mb-md-0 me-md-auto link-dark text-decoration-none"
      >
        <svg className="bi me-2" width="40" height="32">
          <use xlinkHref="#bootstrap" />
        </svg>
        <span className="fs-4">Sidebar</span>
      </a>
      <hr />
      {getSidebarLinks()}
      <hr />
      <div className="dropdown">
        <a
          href="#"
          className="d-flex align-items-center link-dark text-decoration-none dropdown-toggle"
          id="dropdownUser2"
          data-bs-toggle="dropdown"
          aria-expanded="false"
        >
          <img
            src="https://github.com/mdo.png"
            alt=""
            width="32"
            height="32"
            className="rounded-circle me-2"
          />
          <strong>mdo</strong>
        </a>
        <ul
          className="dropdown-menu text-small shadow"
          aria-labelledby="dropdownUser2"
        >
          <li>
            <a className="dropdown-item" href="#">
              Sign out
            </a>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
