import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from "react-router-dom";
import Login from "./components/Login";
import SuperAdminDashboard from "./components/SuperAdminDashboard";
import AdminDashboard from "./components/AdminDashboard";
import NewsEntryDashboard from "./components/NewsEntryDashboard";
import ManageUsers from "./components/ManageUsers";
import Sidebar from "./components/Sidebar";
import NotFoundPage from "."
const ProtectedRoute = ({ component: Component, userRole, ...rest }) => {
  return (
    <Route
      {...rest}
      render={(props) => {
        if (!userRole) {
          // User is not logged in, redirect to login page
          return <Redirect to="/" />;
        } else if (rest.allowedRoles && !rest.allowedRoles.includes(userRole)) {
          // User does not have the correct role for this route, show unauthorized page
          return <div>You don't have access to this page.</div>;
        } else {
          // User is logged in and has the correct role, render the component
          return <Component {...props} />;
        }
      }}
    />
  );
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState("");

  // Check if the user is already logged in (e.g., after a page refresh)
  useEffect(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("userRole");
    if (token && role) {
      setIsLoggedIn(true);
      setUserRole(role);
    }
  }, []);

  const handleLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    setIsLoggedIn(false);
    setUserRole("");
  };

  return (
    <Router>
      <Sidebar userRole={userRole} handleLogout={handleLogout} />
      <Switch>
        <Route exact path="/" render={() => <Login onLogin={handleLogin} />} />
        <ProtectedRoute
          exact
          path="/dashboard/superadmin"
          component={SuperAdminDashboard}
          userRole={userRole}
          allowedRoles={["SuperAdmin"]}
        />
        <ProtectedRoute
          exact
          path="/dashboard/admin"
          component={AdminDashboard}
          userRole={userRole}
          allowedRoles={["Admin"]}
        />
        <ProtectedRoute
          exact
          path="/dashboard/newsentry"
          component={NewsEntryDashboard}
          userRole={userRole}
          allowedRoles={["NewsEntry"]}
        />
        <ProtectedRoute
          exact
          path="/dashboard/manageusers"
          component={ManageUsers}
          userRole={userRole}
          allowedRoles={["SuperAdmin", "Admin"]}
        />
        <Route path="*" component={NotFoundPage} />
      </Switch>
    </Router>
  );
};

export default App;
