import React from "react";
import { BiCategory, BiNews, BiSync } from "react-icons/bi";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faTags, faLanguage } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import ManageUsers from "./components/ManageUsers";
  
const Sidebar = ({ userRole }) => {
  const sidebarLinks = [
    { title: "Categories", icon: <BiCategory size={16} />, link: "/" },
    {
      title: "Tags",
      icon: <FontAwesomeIcon icon={faTags} />,
      link: "/dashboard",
    },
    { title: "News", icon: <BiNews size={16} />, link: "/orders" },

    {
      title: "Languages",
      icon: <FontAwesomeIcon icon={faLanguage} />,
      link: "/languages",
    },
    {
      title: "User Management",
      icon: <FontAwesomeIcon icon={faUser} />,
      link: "/dashboard/manageusers", // Add the link for the User Management page
    },
  ];

  const getSidebarLinks = () => {
    const filteredLinks = sidebarLinks.filter((link) => {
      if (userRole === "SuperAdmin") {
        return true;
      } else if (userRole === "Admin") {
        return link.title !== "User Management"; // Hide User Management link for Admin
      } else {
        return link.title === "Dashboard";
      }
    });

    return (
      <ul className="nav flex-column mb-auto">
        {filteredLinks.map((link) => (
          <li className="nav-item" key={link.title}>
            <Link
              to={link.link}
              className={`nav-link ${
                link.title === "Dashboard" ? "active" : "link-dark"
              }`}
              aria-current={link.title === "Dashboard" ? "page" : undefined}
            >
              <span className="me-2">{link.icon}</span>
              {link.title}
            </Link>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div
      className="d-flex flex-column flex-shrink-0 p-3 bg-light"
      style={{ width: "280px" }}
    >
      <Link
        to="/"
        className="d-flex align-items-center mb-3 mb-md-0 me-md-auto link-dark text-decoration-none"
      >
        <svg className="bi me-2" width="40" height="32">
          <use xlinkHref="#bootstrap" />
        </svg>
        <span className="fs-4">
          <BiSync />
          Sync
        </span>
      </Link>
      <hr />
      {getSidebarLinks()}
      <hr />
    </div>
  );
};

export default Sidebar;
