import React from "react";

const Sidebar = ({ userRole }) => {
  const sidebarLinks = [
    { title: "Home", icon: "home", link: "/" },
    { title: "Dashboard", icon: "speedometer2", link: "/dashboard" },
    { title: "Orders", icon: "table", link: "/orders" },
    { title: "Products", icon: "grid", link: "/products" },
    { title: "Customers", icon: "people-circle", link: "/customers" },
  ];

  const superAdminLinks = [
    { title: "Users", icon: "people-circle", link: "/users" },
  ];

  const getSidebarLinks = () => {
    const filteredLinks = sidebarLinks.filter((link) => {
      if (userRole === "SuperAdmin") {
        return true; // Show all links for SuperAdmin
      } else if (userRole === "Admin") {
        return link.title !== "Users"; // Hide Users link for Admin
      } else {
        return link.title === "Dashboard"; // Show only Dashboard link for NewsEntry
      }
    });

    return (
      <ul className="nav flex-column mb-auto">
        {filteredLinks.map((link) => (
          <li className="nav-item" key={link.title}>
            <a
              href={link.link}
              className={`nav-link ${
                link.title === "Dashboard" ? "active" : "link-dark"
              }`}
              aria-current={link.title === "Dashboard" ? "page" : undefined}
            >
              <svg className="bi me-2" width="16" height="16">
                <use xlinkHref={`#${link.icon}`} />
              </svg>
              {link.title}
            </a>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div
      className="d-flex flex-column flex-shrink-0 p-3 bg-light"
      style={{ width: "280px" }}
    >
      <a
        href="/"
        className="d-flex align-items-center mb-3 mb-md-0 me-md-auto link-dark text-decoration-none"
      >
        <svg className="bi me-2" width="40" height="32">
          <use xlinkHref="#bootstrap" />
        </svg>
        <span className="fs-4">Sidebar</span>
      </a>
      <hr />
      {getSidebarLinks()}
      <hr />
      <div className="dropdown">
        <a
          href="#"
          className="d-flex align-items-center link-dark text-decoration-none dropdown-toggle"
          id="dropdownUser2"
          data-bs-toggle="dropdown"
          aria-expanded="false"
        >
          <strong>mdo</strong>
        </a>
        <ul
          className="dropdown-menu text-small shadow"
          aria-labelledby="dropdownUser2"
        >
          <li>
            <a className="dropdown-item" href="#">
              New project...
            </a>
          </li>
          <li>
            <a className="dropdown-item" href="#">
              Settings
            </a>
          </li>
          <li>
            <a className="dropdown-item" href="#">
              Profile
            </a>
          </li>
          <li>
            <hr className="dropdown-divider" />
          </li>
          <li>
            <a className="dropdown-item" href="#">
              Sign out
            </a>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
