import React from "react";

const Sidebar = ({ userRole }) => {
  const sidebarLinks = {
    commonLinks: [
      { title: "Dashboard", icon: "speedometer2", link: "/dashboard" },
      { title: "News", icon: "newspaper", link: "/news" },
      { title: "Categories", icon: "grid", link: "/categories" },
    ],
    superAdminLinks: [
      { title: "Users", icon: "people-circle", link: "/users" },
    ],
  };

  const renderLinks = (links) => {
    return (
      <ul className="nav nav-pills flex-column mb-auto">
        {links.map((link) => (
          <li className="nav-item" key={link.title}>
            <a href={link.link} className="nav-link" aria-current="page">
              <svg className="bi me-2" width="16" height="16">
                <use xlinkHref={`#${link.icon}`} />
              </svg>
              {link.title}
            </a>
          </li>
        ))}
      </ul>
    );
  };

  const getSidebarLinks = () => {
    const commonLinks = renderLinks(sidebarLinks.commonLinks);

    if (userRole === "SuperAdmin") {
      const superAdminLinks = renderLinks(sidebarLinks.superAdminLinks);
      return (
        <>
          {commonLinks}
          {superAdminLinks}
        </>
      );
    } else {
      return commonLinks;
    }
  };

  return (
    <div
      className="d-flex flex-column flex-shrink-0 p-3 bg-light"
      style={{ width: "280px" }}
    >
      <a
        href="/"
        className="d-flex align-items-center mb-3 mb-md-0 me-md-auto link-dark text-decoration-none"
      >
        <svg className="bi me-2" width="40" height="32">
          <use xlinkHref="#bootstrap" />
        </svg>
        <span className="fs-4">Sidebar</span>
      </a>
      <hr />
      {getSidebarLinks()}
      <hr />
      {/* Logout functionality can be added here */}
    </div>
  );
};

export default Sidebar;
