import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from "react-router-dom";
import Login from "./components/Login";
import SuperAdminDashboard from "./components/SuperAdminDashboard";
import AdminDashboard from "./components/AdminDashboard";
import NewsEntryDashboard from "./components/NewsEntryDashboard";
import ManageUsers from "./components/ManageUsers";
import Sidebar from "./components/Sidebar"; // Import the Sidebar component

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState("");

  // Check if the user is already logged in (e.g., after a page refresh)
  useEffect(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("userRole");
    if (token && role) {
      setIsLoggedIn(true);
      setUserRole(role);
    }
  }, []);

  // Handle successful login
  const handleLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role);
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    setIsLoggedIn(false);
    setUserRole("");
  };

  // Redirect to the appropriate dashboard based on the user's role
  const renderDashboard = () => {
    if (!isLoggedIn) {
      return <Redirect to="/" />;
    } else {
      if (userRole === "SuperAdmin") {
        return <Redirect to="/dashboard/superadmin" />;
      } else if (userRole === "Admin") {
        return <Redirect to="/dashboard/admin" />;
      } else if (userRole === "NewsEntry") {
        return <Redirect to="/dashboard/newsentry" />;
      } else {
        return <Redirect to="/" />;
      }
    }
  };

  return (
    <Router>
      <Switch>
        <Route
          exact
          path="/"
          render={() =>
            isLoggedIn ? renderDashboard() : <Login onLogin={handleLogin} />
          }
        />
        {isLoggedIn && (userRole === "Admin" || userRole === "SuperAdmin") && (
          <Route
            path="/dashboard"
            render={() => (
              <Sidebar userRole={userRole} handleLogout={handleLogout} />
            )}
          />
        )}
      </Switch>
      <Route
        exact
        path="/dashboard/superadmin"
        component={SuperAdminDashboard}
      />
      <Route exact path="/dashboard/admin" component={AdminDashboard} />
      <Route exact path="/dashboard/newsentry" component={NewsEntryDashboard} />
      <Route exact path="/dashboard/manageusers" component={ManageUsers} />
    </Router>
  );
};

export default App;
